function ListGroups({isAdmin, myGroup, user, deletePerson}){
    return (
        <div style={{display: "flex"}}>
      <div style={{
        marginTop: "7%",
        backgroundColor: '#f2f2f2',
        width: '200px',
        height: '100vh',
        position: 'fixed',
        left: '0',
        top: '0',
        overflowY: 'auto',
        padding: '20px',
        boxShadow: '2px 0 5px rgba(0,0,0,0.2)' 

      }}>
      <p>Group Members</p>
      {myGroup.map((people) => (
        <p>{people.Person} {isAdmin && <p onClick={() => deletePerson(people)}>X</p>}</p>
      ))}
      {
        Object.keys(user).length > 1 && 
        <>
        <div>
        <p>--------------------------------</p>
        <p>Their List</p>
        {user['friend'].map((lists) => (
          <p>{lists['item']}</p>
        ))}
        </div>
        <div>
        <p>--------------------------------</p>
        <p>My List</p>
        {user['myself'].map((lists) => (
          <p>{lists['item']}</p>
        ))}
        </div>
        </>
      }
      </div>
      </div>
    )
}

export default ListGroups
