import logo from './logo.svg';
import './App.css';
import axios from 'axios'
import { useState } from 'react' 
function App() {
  const [groupName, setGroupName] = useState('')
  const [groupCode, setGroupCode] = useState('')
  async function startDB(){ 
    console.log(groupName)
    const body = {
      groupName: groupName
    }
    const request = await axios.post('http://localhost:5000/start', body, {
      headers: {
        'Content-Type': 'application/json'
      } 
    });
    console.log(request)
  }
  async function addPerson(){
    const body = {
      groupCode: groupCode
    }
    const request = await axios.post('http://localhost:5000/add', body, {
      headers: {
        'Content-Type': 'application/json'
      } 
    });
    console.log(request)
  }
  return (
    <div className="App">
      <input onChange={(e) => setGroupName(e.target.value)}/>
      <button onClick={startDB}>Click</button>
      <input onChange={(e) => setGroupCode(e.target.value)}/>
      <button onClick={addPerson}>Click</button>
    </div>
  );
}

export default App;
