import logo from './logo.svg';
import './App.css';
import axios from 'axios'
import { useState } from 'react' 
function App() {
  const [groupName, setGroupName] = useState('')
  const [groupCode, setGroupCode] = useState('')
  const [name, setName] = useState('')
  const [signedIn, setSignedIn] = useState(false)
  const [myGroup, setMyGroup] = useState({})
  const [user, setUser] = useState('')
  const [item, setItem] = useState('')
  const [adminCode, setAdminCode] = useState('')
  const [isAdmin, setIsAdmin] = useState(false)
  const [createdNewGroup, setCreatedNewGroup] = useState({})
  const [errorMessage, setErrorMessage] = useState({'CreateGroup': null, 'SignIntoGroup': null})
  async function startDB(){
    if (groupName){
      const body = {
        groupName: groupName
      }
      const request = await axios.post('http://localhost:5000/createGroup', body, {
        headers: {
          'Content-Type': 'application/json'
        } 
      });
      setCreatedNewGroup(request.data)
      setGroupCode(request.data['groupCode'])
    } else {
      setErrorMessage((prevMsg) => {
        const newError = {...prevMsg, [prevMsg['CreateGroup']]: 'Please enter a name for your group'}
        return newError
      })
    }
  }
  async function addPerson(){
    const body = {
      groupCode: myGroup[0].groupCode,
      name: name
    }
    const request = await axios.post('http://localhost:5000/add', body, {
      headers: {
        'Content-Type': 'application/json'
      } 
    });
    if (request.data){
      setUser(request.data)
    }
  }

  async function adminSignIn(){
    const body = {
      groupCode: groupCode,
      adminCode: adminCode
    }
    const request = await axios.post('http://localhost:5000/adminsignin', body, {
      headers: {
        'Content-Type': 'application/json'
      } 
    });
    if (request.data.length >= 1){
      setIsAdmin(true)
    }
  }


  async function Randomize(){
    const url = 'http://localhost:5000/random/' + groupCode
    const request = await axios.get(url);
    console.log(request)
  }

  async function signin(){
    if (groupCode){
      const url = 'http://localhost:5000/signin/' + groupCode
      setCreatedNewGroup({})
      const request = await axios.get(url);
      console.log(request.data)
      if (typeof(request.data) == 'object'){
        setSignedIn(true)
        setMyGroup(request.data)      
      } else if (request.data === 'No group by that code') {
        setErrorMessage((prevMsg) => {
          const newError = {...prevMsg, [prevMsg['SignIntoGroup']]: request.data}
          return newError
        })
      }
    } else {
      console.log('invalid')
      setErrorMessage('Please enter a code')
    }

  }

  async function write_list(){
    const body = {
      groupCode: groupCode,
      user: user['person'],
      item: item
    }
    await axios.post('http://localhost:5000/write_list', body);
    setUser((prevUsers) => {
      const newItem = [...prevUsers['myself'], {personId: user['person'], item: item}]
      const newss = {'friend': prevUsers['friend'], 'myself':newItem}
      return newss
    })
  }
  async function signins(){
    const url = 'http://localhost:5000/signins/' + groupCode
    setCreatedNewGroup({})
    const request = await axios.get(url);
    console.log(request.data)
    if (typeof(request.data) == 'object'){
      setSignedIn(true)
      setMyGroup(request.data)      
    }
  }
  function NewGroup(){
    return(
      <>
      <p>This is your group code DO NOT LOSE IT! {createdNewGroup['groupCode']}</p>
      <p>This is your admin code DO NOT LOSE IT! {createdNewGroup['adminCode']}</p>
      <button className='submit-button' onClick={signins}>Go To Group</button>
      </>
    )
  }

  return (
    <div className="App">
      <link rel="stylesheet" type="text/css" href="//fonts.googleapis.com/css?family=Pacifico" />
      <div className="nav">
    <div id="text" style={{fontFamily: "pacifico", fontSize: "70px"}}>
      Secret Santa
    </div>
  </div>
  <div style={{marginTop: "10%"}}>
    {Object.keys(createdNewGroup).length > 1 &&
    <NewGroup/>
    }
  {!signedIn && 
      <div >
        <div>
        <input onChange={(e) => setGroupName(e.target.value)} placeholder='Create Group' className='input-bar'/>
        {errorMessage['CreateGroup'] && <p>{errorMessage['CreateGroup']}</p>}
        </div>
        <button onClick={startDB} className='submit-button'>Click</button>
        <div>
        <input placeholder='Sign Into Group' onChange={(e) => setGroupCode(e.target.value)} className='input-bar'/>
        </div>
        {errorMessage['SignIntoGroup'] && <p>{errorMessage['SignIntoGroup']}</p>}
        <button onClick={signin} className='submit-button'>Click</button>
      </div>
      }
      {Object.keys(user).length >= 1 && 
      <>
      <div>
      <input placeholder='Write Items For Your List' onChange={(e) => setItem(e.target.value)} className='input-bar'/>
      </div>
      <button onClick={write_list} className='submit-button'>add</button>
      </>
      }
      {signedIn && 
      <>
      <div>
      <input onChange={(e) => setName(e.target.value)} placeholder='Who Are You?' className='input-bar'/>
      </div>
      <button onClick={addPerson} className='submit-button'>Submit</button>
      <div>
      <input onChange={(e) => setAdminCode(e.target.value)} placeholder='Admin Code' className='input-bar'/>
      </div>
      <button onClick={adminSignIn} className='submit-button'>admin sign in</button>

      {isAdmin && <button onClick={Randomize}>Random</button>}
      <div style={{display: "flex"}}>
      <div style={{
        marginTop: "8%",
        backgroundColor: '#f2f2f2',
        width: '200px',
        height: '100vh',
        position: 'fixed',
        left: '0',
        top: '0',
        overflowY: 'auto',
        padding: '20px',
        boxShadow: '2px 0 5px rgba(0,0,0,0.2)' 

      }}>
      <p>Group Members</p>
      {myGroup.map((people) => (
        <p>{people.Person}</p>
      ))}
      {
        Object.keys(user).length > 1 && 
        <>
        <div>
        <p>--------------------------------</p>
        <p>Their List</p>
        {user['friend'].map((lists) => (
          <p>{lists['item']}</p>
        ))}
        </div>
        <div>
        <p>--------------------------------</p>
        <p>My List</p>
        {user['myself'].map((lists) => (
          <p>{lists['item']}</p>
        ))}
        </div>
        </>
      }
      </div>
      <div>
      </div>
      </div>
      </>
      }
  </div>

    </div>
  );
}

export default App;
