import logo from './logo.svg';
import './App.css';
import axios from 'axios'
import { useState } from 'react' 
function App() {
  const [groupName, setGroupName] = useState('')
  const [groupCode, setGroupCode] = useState('')
  const [name, setName] = useState('')
  const [signedIn, setSignedIn] = useState(false)
  const [myGroup, setMyGroup] = useState({})
  const [user, setUser] = useState('')
  const [item, setItem] = useState('')
  const [adminCode, setAdminCode] = useState('')
  const [isAdmin, setIsAdmin] = useState(false)
  async function startDB(){ 
    console.log(groupName)
    const body = {
      groupName: groupName
    }
    const request = await axios.post('http://localhost:5000/start', body, {
      headers: {
        'Content-Type': 'application/json'
      } 
    });
    console.log(request)
  }
  async function addPerson(){
    const body = {
      groupCode: myGroup[0].groupCode,
      name: name
    }
    const request = await axios.post('http://localhost:5000/add', body, {
      headers: {
        'Content-Type': 'application/json'
      } 
    });
    console.log(request.data)
    if (request.data){
      console.log(Object.keys(request.data).length)
      setUser(request.data)
    }
  }

  async function adminSignIn(){
    const body = {
      groupCode: groupCode,
      adminCode: adminCode
    }
    const request = await axios.post('http://localhost:5000/adminsignin', body, {
      headers: {
        'Content-Type': 'application/json'
      } 
    });
    if (request.data.length >= 1){
      setIsAdmin(true)
    }
  }


  async function Randomize(){
    console.log(groupCode)
    const url = 'http://localhost:5000/random/' + groupCode
    const request = await axios.get(url);
    console.log(request)
  }

  async function signin(){
    console.log(groupCode)
    const url = 'http://localhost:5000/signin/' + groupCode
    const request = await axios.get(url);
    console.log(request.data)
    if (typeof(request.data) == 'object'){
      setSignedIn(true)
      setMyGroup(request.data)      
    }
  }

  async function write_list(){
    const body = {
      groupCode: groupCode,
      user: user['friend'][0].personId,
      item: item
    }
    const request = await axios.post('http://localhost:5000/write_list', body);
    console.log(request)
  }

  return (
    <div className="App">
      <link rel="stylesheet" type="text/css" href="//fonts.googleapis.com/css?family=Pacifico" />
      <div className="nav">
    <div id="text" style={{fontFamily: "pacifico", fontSize: "70px"}}>
      Secret Santa
    </div>
  </div>
  <div style={{marginTop: "10%"}}>
  {!signedIn && 
      <div >
        <div>
        <input onChange={(e) => setGroupName(e.target.value)} placeholder='create group' style={{marginTop: '10%', height: '40px', fontSize: '20px', width: '13%', borderRadius: '15px', borderColor: 'white'}}/>
        </div>
        <button onClick={startDB} style={{borderRadius: '5px',
  border: 'none',
  width: '7%',
  fontSize: '120%',
  paddingTop: '.25%',
  paddingBottom: '.25%',
  marginTop: '1%',
  backgroundColor: 'white'}}>Click</button>
        <div>
        <input placeholder='Sign into group' onChange={(e) => setGroupCode(e.target.value)} style={{marginTop: "3%", height: '40px', fontSize: '20px', width: '13%', borderRadius: '15px', borderColor: 'white'}}/>

        </div>
        <button onClick={signin} style={{borderRadius: '5px',
  border: 'none',
  width: '7%',
  fontSize: '120%',
  paddingTop: '.25%',
  paddingBottom: '.25%',
  marginTop: '1%',
  backgroundColor: 'white'}}>Click</button>
      </div>
      }
      {Object.keys(user).length >= 1 && 
      <>
      <input placeholder='Write items for your list' onChange={(e) => setItem(e.target.value)} style={{marginTop: "3%", height: '40px', fontSize: '20px', width: '13%', borderRadius: '15px', borderColor: 'white'}}/>
      <button onClick={write_list}>add</button>
      </>
      }
      {signedIn && 
      <>
      <div>
      <input onChange={(e) => setName(e.target.value)} placeholder='set person name' style={{marginTop: "3%", height: '40px', fontSize: '20px', width: '13%', borderRadius: '15px', borderColor: 'white'}}/>
      </div>
      <div>
      <input onChange={(e) => setAdminCode(e.target.value)} placeholder='Admin code' style={{marginTop: "3%", height: '40px', fontSize: '20px', width: '13%', borderRadius: '15px', borderColor: 'white'}}/>
      </div>
      <button onClick={adminSignIn} style={{borderRadius: '5px',
  border: 'none',
  width: '7%',
  fontSize: '120%',
  paddingTop: '.25%',
  paddingBottom: '.25%',
  marginTop: '1%',
  backgroundColor: 'white'}}>admin sign in</button>
      <button onClick={addPerson} style={{borderRadius: '5px',
  border: 'none',
  width: '7%',
  fontSize: '120%',
  paddingTop: '.25%',
  paddingBottom: '.25%',
  marginTop: '1%',
  backgroundColor: 'white'}}>add</button>
      {isAdmin && <button onClick={Randomize}>Random</button>}
      <div style={{display: "flex"}}>
      <div style={{
        marginTop: "8%",
        backgroundColor: '#f2f2f2',
        width: '200px',
        height: '100vh',
        position: 'fixed',
        left: '0',
        top: '0',
        overflowY: 'auto',
        padding: '20px',
        boxShadow: '2px 0 5px rgba(0,0,0,0.2)' 

      }}>
      <p>Group Members</p>
      {myGroup.map((people) => (
        <p>{people.Person}</p>
      ))}
      </div>
      <div>
        {console.log(Object.keys(user).length)}
      {
        Object.keys(user).length > 1 && 
        <div style={{display: "flex"}}>
        <div style={{marginLeft: "30%"}}>
        <p>Their List</p>
        {user['friend'].map((lists) => (
          <p>{lists['item']}</p>
        ))}
        </div>
        <div style={{marginLeft: "30%"}}>
        <p>My List</p>
        {user['myself'].map((lists) => (
          <p>{lists['item']}</p>
        ))}
        </div>
        </div>
      }
      </div>
      </div>

      </>
      }
  </div>

    </div>
  );
}

export default App;
